components.calculateValue:
value = getFieldValue(data.lonnForForsteUttak)
 	+ getFieldValue(data.feriepengerOpptjentForForsteUttak)
	+ getFieldValue(data.etterbetalingAvLonnOpptjentForForsteUttak)
	+ getFieldValue(data.bonusOpptjentForForsteUttak)
  + getFieldValue(data.sykpenger)
	+ getFieldValue(data.belop)
	+ getFieldValue(data.flereAndreTyperInntekter.belop1)
; 

function getFieldValue(fieldValue) { 
 return parseFloat(fieldValue || 0); 
}
