var startday = new Date(row.fraDatoDdMmAaaa);
var endday = new Date(row.tilDatoDdMmAaaa);
var diffInTime = endday.getTime() - startday.getTime();
var diffInDays = diffInTime/ (1000 * 3600 * 24);

if (endday<=startday)
  {valid = ((endday>startday) ? true : 'Til dato må være større enn Fra dato');}
else 
  {if (diffInDays > 365)
    {valid = ((diffInDays <= 365) ? true : 'Perioden kan være maksimalt ett år');}
  }
